const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET-USERS';


let initialState = {
   users : [
        {id: 1, followed: false, fullName: 'Alex Ivanov', status: 'Boss', location: {country: 'Ukraine', city: 'Kiyv'}, imgSrc: ''}, 
        {id: 2, followed: true, fullName: 'Olga Petrova', status: 'Student', location: {country: 'Ukraine', city: 'Dnepr'}}, 
        {id: 3, followed: false, fullName: 'Katya Sidorova', status: 'Hiii', location: {country: 'Poland', city: 'Krakow'}},
    ],

}

export const usersReducer = (state = initialState, action) => {
   // debugger;
    switch (action.type) {
        case FOLLOW: {
           let stateCopy = {
               ...state,
                users: state.users.map(user=>{
                    if (user.id === action.id) {
                        return {...user, followed: true}
                    }
                    return user;
                })
           }
        }

        case UNFOLLOW : {
            let stateCopy = {
                ...state,
                 users: state.users.map(user=>{
                     if (user.id === action.id) {
                         return {...user, followed: false}
                     }
                     return user;
                 })
            }
        }

        case SET_USERS : {
            return {
                ...state, 
                users: [...state.users, ...action.users]
            }
        }
 
        default: return state;    
    }
}

export const followAC = (userId) => ({ type: FOLLOW, userId })
export const unfollowAC = (userId) => ({ type: UNFOLLOW, userId})
export const setUsersAC = (users) => ({ type: SET_USERS, users})

export default usersReducer;
